import React from 'react';
import './SocialMedia.css';

const SocialMedia = () => {
  const socialLinks = [
    {
      name: 'Website',
      url: 'http://tunisiagbc.org/',
      icon: 'üåê',
      color: '#4ade80'
    },
    {
      name: 'Facebook',
      url: 'https://www.facebook.com/TunisiaGBC',
      icon: 'üìò',
      color: '#1877F2'
    },
    {
      name: 'LinkedIn',
      url: 'https://www.linkedin.com/company/tunisia-green-building-council/posts/?feedView=all',
      icon: 'üíº',
      color: '#0077B5'
    },
    {
      name: 'Instagram',
      url: 'https://www.instagram.com/tunisiagbc/',
      icon: 'üì∑',
      color: '#E4405F'
    }
  ];

  return (
    <div className="social-media-container">
      <div className="social-links">
        {socialLinks.map((social, index) => (
          <a
            key={index}
            href={social.url}
            target="_blank"
            rel="noopener noreferrer"
            className="social-link"
            style={{ '--social-color': social.color }}
          >
            <span className="social-icon">{social.icon}</span>
            <span className="social-name">{social.name}</span>
          </a>
        ))}
      </div>

      <div className="contact-email">
        <p>üìß Contact: info@tunisiagbc.org</p>
      </div>
    </div>
  );
};

export default SocialMedia;
