import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import './Header.css';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isLanguageMenuOpen, setIsLanguageMenuOpen] = useState(false);
  const [currentLanguage, setCurrentLanguage] = useState('FR');
  const location = useLocation();

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  const toggleLanguageMenu = () => {
    setIsLanguageMenuOpen(!isLanguageMenuOpen);
  };

  const selectLanguage = (lang) => {
    setCurrentLanguage(lang);
    setIsLanguageMenuOpen(false);
  };

  return (
    <header className="header">
      <div className="header-container">
        <Link to="/" className="logo-section" onClick={closeMenu}>
          <img 
            src="/TGBC.jpg" 
            alt="TUNISIAGBC Logo" 
            className="logo-image"
          />
        </Link>

        <nav className={`nav ${isMenuOpen ? 'nav-open' : ''}`}>
          <ul className="nav-list">
            <li className="nav-item">
              <Link 
                to="/about" 
                className={`nav-link ${location.pathname === '/about' ? 'active' : ''}`}
                onClick={closeMenu}
              >
                Le TGBC
              </Link>
            </li>
            <li className="nav-item">
              <Link 
                to="/members" 
                className={`nav-link ${location.pathname === '/members' ? 'active' : ''}`}
                onClick={closeMenu}
              >
                Adhésion
              </Link>
            </li>
            <li className="nav-item">
              <Link 
                to="/programs" 
                className={`nav-link ${location.pathname === '/programs' ? 'active' : ''}`}
                onClick={closeMenu}
              >
                Programmes
              </Link>
            </li>
            <li className="nav-item">
              <Link 
                to="/events" 
                className={`nav-link ${location.pathname === '/events' ? 'active' : ''}`}
                onClick={closeMenu}
              >
                Agenda
              </Link>
            </li>
            <li className="nav-item">
              <Link 
                to="/resources" 
                className={`nav-link ${location.pathname === '/resources' ? 'active' : ''}`}
                onClick={closeMenu}
              >
                Ressources
              </Link>
            </li>
          </ul>

          <div className="header-actions">
            <Link to="/contact" className="btn btn-primary" onClick={closeMenu}>
              Devenir membre
            </Link>
            <div className="language-switcher">
              <button 
                className={`lang-btn ${currentLanguage === 'FR' ? 'active' : ''}`}
                onClick={() => toggleLanguage('FR')}
                title="Français"
              >
                🌍 FR
              </button>
              <button 
                className={`lang-btn ${currentLanguage === 'EN' ? 'active' : ''}`}
                onClick={() => toggleLanguage('EN')}
                title="English"
              >
                🌍 EN
              </button>
            </div>
          </div>
        </nav>

        <button 
          className={`menu-toggle ${isMenuOpen ? 'menu-open' : ''}`}
          onClick={toggleMenu}
          aria-label="Toggle menu"
        >
          <span></span>
          <span></span>
          <span></span>
        </button>
      </div>
    </header>
  );
};

export default Header;
